function fadeInPage(){window.AnimationEvent&&document.getElementById("fader").classList.add("fade-out")}document.addEventListener("DOMContentLoaded",function(){if(window.AnimationEvent){for(var e=document.getElementsByTagName("a"),n=0;n<e.length;n+=1)e[n].hostname===window.location.hostname&&e[n].pathname!==window.location.pathname&&e[n].addEventListener("click",function(e){var n=document.getElementById("fader"),t=e.currentTarget,a=function(){window.location=t.href,n.removeEventListener("animationend",a)};n.addEventListener("animationend",a),e.preventDefault(),n.classList.add("fade-in")});for(var t=document.getElementsByTagName("img"),a=0;a<t.length;a+=1)t[a].parentElement.classList.add("full")}}),window.addEventListener("pageshow",function(e){e.persisted&&document.getElementById("fader").classList.remove("fade-in")});